def get_values_key(d, pattern):
    values = []
    for k in d.keys():
        if pattern in k:
            values += [d[k]]
    return list(set(values))

def image_obj(url):
    image = {"image":{ "source":{ "imageUri": url }},
             "features":[
                { "type":"TEXT_DETECTION"},
                { "type":"LABEL_DETECTION"},
                { "type":"LOGO_DETECTION"}
            ]}
    return image



class ModJSON(StreamCallback):
  def __init__(self):
        pass

  def process(self, inputStream, outputStream):
    text = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    obj = json.loads(text)

    media_list = []
    image_reqs = []

    media_list = get_values_key(obj,"media_url_https")

    if len(media_list) == 0 :
        log.warn("No media!!")
        raise Exception()
    else:
        for ci in media_list:
            image_reqs += [image_obj(ci)]


    newObj = {
        "requests": image_reqs,
        }
    outputStream.write(bytearray(json.dumps(newObj, indent=4).encode('utf-8')))

flowFile = session.get()
if (flowFile != None):
    try:
        flowFile = session.write(flowFile, ModJSON())
        flowFile = session.putAttribute(flowFile, "filename", flowFile.getAttribute('filename').split('.')[0]+'vision.json')
        session.transfer(flowFile, REL_SUCCESS)
        session.commit()
    except:
        log.warn('No media')
        session.transfer(flowFile, REL_FAILURE)
